multibranchPipelineJob('CodeValidation_CI') {
	branchSources {
		github {
			id('1f6f9ade-1bad-45fe-8712-16899206cb21')
			apiUri('https://github.wdf.sap.corp/api/v3/')
			repoOwner('DevX')
			repository('CodeValidation')
			includes('master')
			scanCredentialsId('GitHub_User')
		}
	}

	configure { project ->
		project / 'factory'  {
			scriptPath 'Jenkinsfile_CI'
		}
	}

	orphanedItemStrategy {
		discardOldItems {
			numToKeep(50)
		}
	}
}
